mapper.select:
select
,r.article_num as 'attr.articleNum', r.follower_num as 'attr.followerNum' from biz_user as a join biz_user_attr as r on r.user_id=a.uuid where a.del_flag=0
and a.uuid=#{uuid}
and a.login_name=#{loginName}

mapper.update:
update biz_user 
set del_flag=1 
where uuid=#{uuid}

mapper.select:
select count(*) from biz_user as a
where
a.del_flag=0

    and a.uuid=#{uuid}


    and a.login_name=#{loginName}

select
,a.password as password from biz_user as a where a.del_flag=0
and a.uuid=#{uuid}
and a.login_name=#{loginName}
